package xmss

import (
	"bytes"
	"encoding/hex"
	"runtime"
	"testing"

	"github.com/AidosKuneen/numcpu"
)

func TestXMSS(t *testing.T) {
	n := numcpu.NumCPU()
	npref := runtime.GOMAXPROCS(n)
	t.Log(n)
	skseed, err := hex.DecodeString("b041bf7ca73cc7905aadc1b6460da2e50206652e3d57a61487beb09664da308d")
	if err != nil {
		t.Fatal(err)
	}
	skprf, err := hex.DecodeString("303f46b2e4248b040a74e7f1eca3ff288b452ef4910739b466225ccadcdb3aae")
	if err != nil {
		t.Fatal(err)
	}
	pubseed, err := hex.DecodeString("115e28fb34d6414cd7cce714895df8c4a674ee1e557f1f3df99370aa8ab7c404")
	if err != nil {
		t.Fatal(err)
	}
	sk, pk := NewXMSSKeyPairWithParams(10, skseed, skprf, pubseed, 0, 0)
	if hex.EncodeToString(sk.root) != "a959a891573da8633b89e8f21e43eef9fca43a14bd2d71b1cf9ad5706945e752" {
		t.Error("root of xmss is incorrect")
		t.Log(hex.EncodeToString(sk.root))
	}

	msg, err := hex.DecodeString("")
	if err != nil {
		t.Fatal(err)
	}

	sig := sk.Sign(msg)
	csig, err := hex.DecodeString("00000000" +
		"")
	if err != nil {
		t.Fatal(err)
	}
	csig = csig[:len(csig)-len(msg)]
	if !bytes.Equal(sig, csig) {
		t.Error(hex.EncodeToString(sig))
		t.Error("XMSS sig is incorrect")
		csigstr, err2 := bytes2sig(csig, 10)
		if err2 != nil {
			t.Error(err2)
		}
		sigstr, err2 := bytes2sig(sig, 10)
		if err2 != nil {
			t.Error(err2)
		}
		if csigstr.index != sigstr.index {
			t.Error("sig index is incorrect")
		}
		if !bytes.Equal(csigstr.r, sigstr.r) {
			t.Error("sig ris incorrect")
		}
		for i := range csigstr.sig {
			if !bytes.Equal(csigstr.sig[i], sigstr.sig[i]) {
				t.Error("sig seed incorrect")
			}
		}
		for i := range csigstr.auth {
			if !bytes.Equal(csigstr.auth[i], sigstr.auth[i]) {
				t.Error("sig auth incorrect", i)
				t.Log(hex.EncodeToString(csigstr.auth[i]))
				t.Log(hex.EncodeToString(sigstr.auth[i]))
			}
		}
	}
	if !pk.Verify(sig, msg) {
		t.Error("XMSS verification is incorrect")
	}

	msg2, err := hex.DecodeString("")
	if err != nil {
		t.Fatal(err)
	}
	sig2 := sk.Sign(msg2)
	csig2, err := hex.DecodeString("00000001" +
		"")
	if err != nil {
		t.Fatal(err)
	}
	csig2 = csig2[:len(csig2)-len(msg2)]
	if !bytes.Equal(sig2, csig2) {
		t.Error("xmss sig2 is incorrect")
	}
	if !pk.Verify(sig2, msg2) {
		t.Error("XMSS verification 2 is incorrect")
	}
	runtime.GOMAXPROCS(npref)
}

func TestXMSSImport(t *testing.T) {
	privKeyExport := &PrivateKeyExport{
		PublicKeyExport: PublicKeyExport{
			XMSSParameters: XMSSParameters{Height: 10},
			PublicSeed:     []byte("TestPublicSeed"),
			Root:           []byte("TestRoot"),
		},
		Index:         0,
		SecretKeySeed: []byte("TestSecretKeySeed"),
		SecretKeyPRF:  []byte("TestSecretKeyPRF"),
	}

	key := new(PrivateKey)
	key.Import(privKeyExport)

	pubKeyExport := &PublicKeyExport{
		XMSSParameters: XMSSParameters{Height: 10},
		PublicSeed: []byte("TestPublicSeed"),
		Root: []byte("TestRoot"),
	}

	pubKey := new(PublicKey)
	pubKey.Import(pubKeyExport)
}
